import { Col, Container, Row, Form, Button} from "react-bootstrap";
import {useNavigate} from 'react-router-dom';
import React, {useEffect, useState} from "react";

function AddItem()
{

    let navigate = useNavigate();
    let user = JSON.parse(localStorage.getItem('user-info'))
    const [title, setTitle]=useState("")
    const [desc, setDesc]=useState("Bardzo smaczna potrawa")
    const [quantity, setQuantity]=useState("")
    const [price, setPrice]=useState("")
    const [unit, setUnit]=useState("")
    const [restaurant, setRestaurant]=useState("")
    const [restaurantId, setRestaurantId]=useState("")
    const [restaurantName, setRestaurantName]=useState("")
    const [itemType, setItemType]=useState("1")
    const [itemTypeName, setItemTypeName]=useState("test")

    useEffect (()=>{
        async function fetchData(){
        let data = await fetch("http://localhost:8080/restaurant//get-restaurants?ownerId=" + user.id);
            data = await data.json()
            console.warn(data.value)

            if(data.status === 1){
                data = await data.value
                setRestaurant(data)
            }
            else {

            }

        }
        fetchData();
        console.warn(JSON.stringify(restaurant))
    },[]);

    async function create(){

        let item={title, desc, quantity, price, unit, restaurantId, restaurantName, itemType, itemTypeName}
        console.warn(item)

        // let result = await fetch("http://localhost:8080/item/create/",{
        //     method:'PUT',
        //     body:JSON.stringify(item),
        //     headers:{
        //         "Content-Type":'application/json',
        //     }
        // })

        // result = await result.json()

        // if(result.status === 1){
        //     navigate('/')
        // }
        // else{
        //     console.warn(result) 
        //     console.warn(result.errorList)
        // }
    }

    return(
        <div>
            <Container>
                <Row className="justify-content-center mt-5">
                    <Col sm={12}><h2>Dodaj pozycję menu</h2></Col> 
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Nazwa</Form.Label>
                                <Form.Control type="input" value={title} onChange={(e)=>setTitle(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Nazwisko</Form.Label>
                                <Form.Control type="input" value={lastName} onChange={(e)=>setLastName(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <div className="clearfix"></div>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Numer telefonu</Form.Label>
                                <Form.Control type="input" value={phoneNumber} onChange={(e)=>setPhone(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Adres email</Form.Label>
                                <Form.Control type="input" value={email} onChange={(e)=>setEmail(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <div className="clearfix"></div>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Hasło</Form.Label>
                                <Form.Control type="password" value={password} onChange={(e)=>setPassword(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Płaca</Form.Label>
                                <Form.Control type="input" value={salary} onChange={(e)=>setSalary(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <div className="clearfix"></div>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Restauracja</Form.Label>
                                <Form.Control type="input" value={restaurantId} onChange={(e)=>setRestaurantId(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Rola</Form.Label>
                                <Form.Control type="input" value={employeeRoleId} onChange={(e)=>setEmployeeRoleId(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <Col sm={12} md={4}>
                        <Form.Group className="mb-3">
                                <Form.Label className="float-start">Opis</Form.Label>
                                <Form.Control as="textarea" rows={3} value={desc} onChange={(e)=>setDesc(e.target.value)}/>
                            </Form.Group>
                        </Col>
                        <Col sm={12} md={4}>
                            <Form.Group className="mb-3">
                                <Form.Label className="float-start">Restauracja</Form.Label>
                                <Form.Select aria-label="Default select example" value={restaurantName} onChange={(e)=>{setRestaurantId(e.target.id.value);setRestaurantName(e.target.name.value)}}>
                                    {
                                        restaurant.map((opt)=>
                                            <option value={opt}>{opt.name}</option>
                                        )
                                    }
                                </Form.Select>
                            </Form.Group>
                        </Col>
                        <Col sm={12}>
                        <Button variant="danger" className="mb-5" onClick={create}>
                            Dodaj
                        </Button>
                        </Col>
                </Row>
            </Container> 
        </div>
    )
}

export default AddItem
